  '$geoNear' : { 
    'near' : {
      'type': 'Point',
      'coordinates' : [:-:lon:-:, :-:lat:-:]
    },
    'distanceField' : 'distance',
    'distanceMultiplier' : 1,
    'maxDistance' : 5000,
    'spherical' : true,
    'query' : { 
      '$or' : [
        { ts: 'amenity=pub' },
