$doc = [
    'tags' => [
        'name=A440',
        'highway=secondary',
        'oneway=yes'
    ]
];
$db->poi->createIndex( [ 'tags' => 1 ] );

// Road with name=Strand
$db->poi->find( [ 'tags' => 'name=Strand' ] );
// All roads
$db->poi->find( [ 'tags' => new \MongoDB\BSON\Regex( '/^highway=/' ) ] );
